function dxTextFigma(text, leftX, topY, rightX, bottomY, color, scaleXY, scaleY, font, alignX, alignY, clip, wordBreak, postGUI, colorCoded, subPixelPositioning, fRotation, fRotationCenterX, fRotationCenterY, fLineSpacing)
    dxDrawText(text, leftX, topY, (leftX + rightX), (topY + bottomY), color, scaleXY, scaleY, font, alignX, alignY, clip, wordBreak, postGUI, colorCoded, subPixelPositioning, fRotation, fRotationCenterX, fRotationCenterY, fLineSpacing)    
end
